        ")\n",
        "\n",
        "# Drop rows with missing company names and get the most frequent company\n",
        "top_company = (\n",
        "    users_df.dropna(subset=['company'])\n",
        "    .company.value_counts()\n",
        "    .idxmax()\n",
        ")\n",
        "\n",
        "print(top_company)\n",
        "\n"
